<?php

namespace SM\Bundle\AdminBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\UnitOfWork;

/**
 * MediaRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class MediaRepository extends EntityRepository
{
    /**
     * @param type $limit    limit
     * @param type $offset   offset
     * @param type $criteria criteria
     * @param type $orderBy  orderBy
     *
     * @return type
     */
    public function getList($limit = null, $offset = null, $criteria = array(), $orderBy = array())
    {
        return $this->findBy($criteria, $orderBy, $limit, $offset);
    }


    /**
     * Delete language by array id
     *
     * @param array $ids
     *
     * @return array
     */
    public function deleteByIds($ids = array())
    {
        $em = $this->getEntityManager();
        $rst = array();
        if (is_array($ids) && count($ids)) {
            foreach ($ids as $id) {
                $entity = $this->find($id);
                $image = $entity->getName();
                $em->remove($entity);
                if ($em->getUnitOfWork()->getEntityState($entity) == UnitOfWork::STATE_REMOVED) {
                    \SM\Bundle\AdminBundle\Utilities\File::deleteImages($image);
                    $rst[] = $id;
                }
            }
            $em->flush();
        }

        return $rst;
    }

    /**
     * get total
     *
     * @return type
     */
    public function getTotal()
    {
        $rst = $this->findAll();

        return count($rst);
    }

    /**
     * @return type
     */
    public function getArrayOptions()
    {
        $options = array();
        $rst = $this->getList(null, null, array('active' => 1));
        if (is_array($rst) && count($rst) > 0) {
            foreach ($rst as $ent) {
                $options[$ent->getId()]['id'] = $ent->getId();
                $options[$ent->getId()]['name'] = $ent->getName();
            }
        }

        return $options;
    }

    public function getObjectOptionByCatId($catId)
    {
        $options = array();
        if (!empty($catId)) {
            $rst = $this->getList(null, null, array('active' => '1', 'category' => $catId));
            if (is_array($rst) && count($rst) > 0) {
                foreach ($rst as $obj) {
                    $std = new \stdClass();
                    $std->id = $obj->getId();
                    $std->name = $obj->getName();
                    $std->catId = $obj->getCategory()->getId();
                    $options[] = $std;
                }
            }

        }
        return $options;
    }
}
